mapper.select:
SELECT c1.id,c1.channelId,c1.channelName,c1.payType,c1.price,c1.parentId,c1.day,c1.click,c1.display,c1.activity,c1.createTime,c1.updateTime,c2.channelName AS parentChannelName, CASE c1.payType WHEN 0 THEN c1.display/1000*c1.price WHEN 1 THEN c1.click*c1.price WHEN 2 THEN c1.activity*c1.price ELSE 0 END AS consume FROM channellog c1 LEFT OUTER JOIN channel c2 ON c1.parentId = c2.id
c1.channelId = #{channelId}
AND c1.day >= #{startDay}
AND c1.day <= #{endDay}

SELECT day,SUM(activity) AS totalActivity,SUM(display) AS totalDisplay,SUM(click) AS totalClick,
    SUM(CASE payType
        WHEN 0 THEN display/1000*price
        WHEN 1 THEN click*price
        WHEN 2 THEN activity*price
        ELSE 0 END
    ) AS totalConsume
FROM channellog


        channelId = #{channelId}



    AND day >= #{startDay}




    AND day <= #{endDay}



GROUP BY day

select c1.totalActivity,c1.totalDisplay,c1.totalClick,c1.totalConsume,c2.channelName,c2.price,c2.payType,c3.channelName as parentChannelName from (select channelId,SUM(activity) AS totalActivity,SUM(display) AS totalDisplay,SUM(click) AS totalClick, SUM(CASE payType WHEN 0 THEN display/1000*price WHEN 1 THEN click*price WHEN 2 THEN activity*price ELSE 0 END ) AS totalConsume from channellog
channelId = #{channelId}
AND channelName like #{channelName}
group by channelId) as c1 left outer join channel c2 on c1.channelId = c2.id left outer join channel c3 on c2.parentId = c3.id
